Matrix* create_matrix(int rows, int cols) {
    Matrix *mat = (Matrix*)malloc(sizeof(Matrix));
    if (!mat) return NULL;

    mat->rows = rows;
    mat->cols = cols;
    mat->data = (double*)calloc(rows * cols, sizeof(double));
    if (!mat->data) {
        free(mat);
        return NULL;
    }

    return mat;
}

void free_matrix(Matrix *mat) {
    if (mat) {
        if (mat->data) free(mat->data);
        free(mat);
    }
}

int save_matrix_binary(const char *filename, Matrix *mat) {
    FILE *fp = fopen(filename, "wb");
    if (!fp) return -1;

    fwrite(&mat->rows, sizeof(int), 1, fp);
    fwrite(&mat->cols, sizeof(int), 1, fp);
    fwrite(mat->data, sizeof(double), mat->rows * mat->cols, fp);

    fclose(fp);
    return 0;
}

Matrix* load_matrix_binary(const char *filename) {
    FILE *fp = fopen(filename, "rb");
    if (!fp) return NULL;

    int rows, cols;
    if (fread(&rows, sizeof(int), 1, fp) != 1 ||
        fread(&cols, sizeof(int), 1, fp) != 1) {
        fclose(fp);
        return NULL;
    }

    Matrix *mat = create_matrix(rows, cols);
    if (!mat) {
        fclose(fp);
        return NULL;
    }

    size_t elements_read = fread(mat->data, sizeof(double), rows * cols, fp);
    fclose(fp);

    if (elements_read != (size_t)(rows * cols)) {
        free_matrix(mat);
        return NULL;
    }

    return mat;
